import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Cliente } from 'src/app/domain/cliente';
import { Ticket } from 'src/app/domain/ticket';
import { TicketService } from 'src/app/services/ticket.service';

@Component({
  selector: 'app-ticket',
  templateUrl: './ticket.component.html',
  styleUrls: ['./ticket.component.scss']
})
export class TicketComponent {

  ticket: Ticket = {
    numero: 0,
    fechaIngreso: new Date(),
    fechaSalida: new Date(),
    estado: 'Activo',
    vehiculo: {
      placa: '',
      marca: '',
      modelo: '',
      color: ''
    },
    tarifa: {
      id: 0,
      tipo: '',
      monto: 0
    }
  };

  constructor(public ticketService: TicketService, private router: Router) { }
  listaVehiculos: any[] = []; // Asegúrate de llenar esta lista con los vehículos disponibles
  listaTarifas: any[] = []; // Asegúrate de llenar esta lista con las tarifas disponibles

  listadoTicketsWS: any[] = [];

  ngOnInit(): void {
    this.listarTickets();
  }

  guardarT1() {
    // Aquí puedes realizar la lógica para guardar los datos ingresados en el formulario
    // Por ejemplo, podrías enviar los datos a través de un servicio HTTP para guardarlos en el backend.
    // También puedes realizar otras validaciones o acciones necesarias antes de guardar.
  }

  agregarTicket() {
    this.ticketService.save(this.ticket).subscribe(
      response => {
        this.ticket = {numero: 0, fechaIngreso: new Date(), fechaSalida: new Date(), estado: '',
          vehiculo: {
            placa: '',
            marca: '',
            modelo: '',
            color: ''
          },
          tarifa: {
            id: 0,
            tipo: '',
            monto: 0
          }
        };
        this.router.navigate(['registrarTicket']);
        this.listarTickets();
      },
      error => {
        console.log("${error}");
      }
    );
  }


  public listarTickets() {
    this.ticketService.obtenerVehiculos().subscribe(
      (response) => {
        this.listadoTicketsWS = response;
        console.log('Listado de contactos:', this.listadoTicketsWS);
      },
      (error) => {
        console.error('Error al obtener la lista de contactos:', error);
      }
    );
  }

  horaSalida: string = '';
  horaActual: string = this.obtenerHoraActual();

  obtenerFechaActual(): string {
    const fechaActual = new Date();
    return fechaActual.toISOString().slice(0, 19); // Para obtener el formato YYYY-MM-DDTHH:mm:ss
  }

  obtenerHoraActual(): string {
    const fechaActual = new Date();
    return `${fechaActual.getHours().toString().padStart(2, '0')}:${fechaActual.getMinutes().toString().padStart(2, '0')}:${fechaActual.getSeconds().toString().padStart(2, '0')}`;
  }

}
