import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Factura } from 'src/app/domain/factura';
import { FacturaService } from 'src/app/services/factura.service';

@Component({
  selector: 'app-factura',
  templateUrl: './factura.component.html',
  styleUrls: ['./factura.component.scss']
})
export class FacturaComponent {
  factura: Factura = {
    numero: 0,
    fecha: this.obtenerHoraActual(), // Permitimos que el campo sea de tipo string o null
    total: 0,
    cliente: {
      apellido: "Peralta",
      cedula: "0105322291",
      direccion: "Octavio",
      nombre: "Danny",
      telefono: "0998446050"
  },
    ticket: {
      estado: "Activo",
      fechaIngreso: "18:05:35",
      fechaSalida: "14:30:00",
      numero: 1,
      tarifa: {
          id: 1,
          monto: 1.5,
          tipo: "Normal"
      },
      vehiculo: {
          color: "Plateado",
          marca: "Chevrolet",
          modelo: "Corsa Evo",
          placa: "AFT0976"
      }
  }
  };

  constructor(public facturaService: FacturaService, private router: Router) { }
  listadoTicketsWS: any[] = [];

  ngOnInit(): void {
    this.listarTickets();
  }


  public listarTickets() {
    this.facturaService.obtenerVehiculos().subscribe(
      (response) => {
        this.listadoTicketsWS = response;
        console.log('Listado de contactos:', this.listadoTicketsWS);
      },
      (error) => {
        console.error('Error al obtener la lista de contactos:', error);
      }
    );
  }

  obtenerHoraActual(): string {
    const fechaActual = new Date();
    const hora = String(fechaActual.getHours()).padStart(2, '0');
    const minutos = String(fechaActual.getMinutes()).padStart(2, '0');
    const segundos = String(fechaActual.getSeconds()).padStart(2, '0');
    return `${hora}:${minutos}:${segundos}`;
  }

}
