import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { Ticket } from 'src/app/domain/ticket';
import { TicketService } from 'src/app/services/ticket.service';

@Component({
  selector: 'app-ticket',
  templateUrl: './ticket.component.html',
  styleUrls: ['./ticket.component.scss']
})
export class TicketComponent {

  ticket: Ticket = {
    numero: 0,
    fechaIngreso: '',
    fechaSalida: '',
    estado: 'Activo',
    vehiculo: {
      placa: '',
      marca: '',
      modelo: '',
      color: ''
    },
    tarifa: {
      id: 0,
      tipo: '',
      monto: 0
    }
  };

  horaActual: string | undefined; // Nueva propiedad para la hora actual

  constructor(public ticketService: TicketService, private router: Router) { }
  listadoTicketsWS: any[] = [];

  ngOnInit(): void {
    this.listarTickets();


  }



  agregarTicket() {
    const horaActual = new Date().toLocaleTimeString('es-EC', { hour12: false });
    print(horaActual.toString);
    // Asignar la hora actual a fechaIngreso y fechaSalida
    this.ticket.fechaIngreso = horaActual;
    this.ticket.fechaSalida = horaActual;

    this.ticketService.save(this.ticket).subscribe(
      response => {
        this.ticket = {numero: 0, fechaIngreso: '', fechaSalida: '', estado: '',
          vehiculo: {
            placa: '',
            marca: '',
            modelo: '',
            color: ''
          },
          tarifa: {
            id: 0,
            tipo: '',
            monto: 0
          }
        };
        this.router.navigate(['registrarTicket']);
        this.listarTickets();
      },
      error => {
        console.log("${error}");
      }
    );
  }

  public listarTickets() {
    this.ticketService.obtenerVehiculos().subscribe(
      (response) => {
        this.listadoTicketsWS = response;
        console.log('Listado de contactos:', this.listadoTicketsWS);
      },
      (error) => {
        console.error('Error al obtener la lista de contactos:', error);
      }
    );
  }

}
